mapper.select:
select IFNULL(sum(file_size), 0)
from file_info
where user_id = #{userId}

mapper.update:
update file_info
set file_md5 = #{fileInfo.fileMd5}, file_size = #{fileInfo.fileSize},
    file_cover = #{fileInfo.fileCover}, file_size = #{fileInfo.fileSize},
    status = #{fileInfo.status}, last_update_time = #{fileInfo.lastUpdateTime}
where
    file_id = #{fileInfo.fileId} and user_id = #{fileInfo.userId}
    and status = #{oldStatus}

mapper.select:
select 
from file_info
where user_id = #{userId} and file_id = #{fileId}
